import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import "./Table.css";
const Table = () => {
  const [allPost, setAllPost] = useState([]);
  const[searchTerm,setsearchTerm]=useState("");
  useEffect(() => {

    fetch("http://localhost:3003/posts")
      .then((res) => {
        return res.json();
      })
      .then((postData) => {
        setAllPost(postData);
      });
  }, []);
  return (
    <>
      <div className="a2">
        <input placeholder="PPP ID" className="PPPID" onChange={(e)=>{
      setsearchTerm(e.target.value);
    }}></input>
        <img src="search.png" className="i1" alt=""></img>
        <a href="/p1">
          <button className="addpropbtn">+ Add Property</button>
        </a>
      </div>
      <div className="tablebody">
        <table className="Table">
          <tr className="inside table">
            <th>PPP ID</th>
            <th>Image</th>
            <th>Property</th>
            <th>contact</th>
            <th>Area</th>
            <th>views</th>
            <th>status</th>
            <th>Days left</th>
            <th>Action</th>
          </tr>
        </table>
      
      
       {allPost.filter((val)=>{
        if(searchTerm===""){
          return val;
        }else{
           return val.PropertyType.toLowerCase().includes(searchTerm.toLowerCase()); 
            val.Facing.toLowerCase().includes(searchTerm.toLowerCase()); 
          

        }
      })
        .map((ele) => {
          return (
        
              <table className="Table">
                <tr className="inside table">
                  <td>{parseInt(Math.random() * 10000)}</td>
                  <td>
                    <img src="img.png" alt="img"></img>
                  </td>
                  <td>{ele.PropertyType}</td>
                  <td>{ele.mobile}</td>
                  <td>{ele.Area}</td>
                  <td>{ele.Facing}</td>
                  <td>{ele.Ownership}</td>
                  <td>{parseInt(Math.random() * 100)}</td>
                  <td>
                    <img className="eyeic" src="i.png" alt=""></img>
                    <img className="editic" src="edit.png" alt=""></img>
                  </td>
                </tr>
              </table>
      
          );
        })}
      </div>
    </>
  );
};

export default Table;
